import { createContext, useLayoutEffect, useState } from "react"



// Creatae context Objext
export const CryptoContext = createContext({})

// Create the provider component
export const CryptoProvider = ({children}) => {
    const [cryptoData, setCryptoData] = useState();
    const [searchData, setSearchData] = useState();
    const [coinSearch, setCoinSearch] = useState("");

   

    const getCryptoData = async() => {

        try {
            const data = await fetch(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&per_page=10&id=${coinSearch}&page=1&price_change_percentage=1h%2C24h%2C7d`)
            .then(res => res.json())
            .then(json => json)
            // console.log(data)
           setCryptoData(data)
        } catch (error) {
            console.log(error)
        }
    }


    const getSearchResult = async(query) => {

        try {
            const data = await fetch(`https://api.coingecko.com/api/v3/search?query=${query}`)
            .then(res => res.json())
            .then(json => json)
            console.log(data)
           setSearchData(data.coins)
        } catch (error) {
            console.log(error)
        }
    }

    useLayoutEffect(() => {
      getCryptoData();
    }, [])
    return(
        <CryptoContext.Provider value={{ cryptoData, searchData, getSearchResult, setCoinSearch }}>
            {children}
        </CryptoContext.Provider>
    )
}
